import React from 'react';

class Form extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      textField:'',
    }
  }

  keyLogger = (e) => {
    e.preventDefault();
    let input = e.target.value;
    this.setState({textField:input});
  }

  submitHandler = (e) => {
    e.preventDefault();
    this.props.addItem(this.state.textField)
    e.target.reset();
  }

  render() {
    return (
      <form onSubmit={this.submitHandler}>
        <input  onChange={this.props.keyLogger} placeholder="I need to...">
        </input>
        <button> Add to List</button>
      </form>
    );
  }

}

export default Form;
