import React from 'react';

class Title extends React.Component {
  constructor(props){
    super(props);
    this.state =  {
      h1Text:'',
    }
  }

  renderText = text => {
    text.toUpperCase()
    this.setState({h1Text:this.props.text})
  }

  render() {
    return <h1>
      {this.renderText(this.state.h1Text)}
    </h1>
  }
}

export default Title;
