import React from 'react';

class Title extends React.Component {
  constructor(props){
    super(props);
    state =  {
      h1Text:this.props.text
    }
  }

  renderText = text => {
    text.toUpperCase()
  }

  render() {
    return <h1>
      {this.renderText(this.props.text)}
    </h1>
  }
}

export default Title;
