import React from 'react';

class Form extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      items:this.props.items,
    }
  }

  addItem = (e) => {
    e.preventDefault();
    console.log()
    this.state.items.push(e.target.value);
    this.setState({list:this.state.items})
  }

  render() {
    return (
      <>
      <form onSubmit={this.addItem}>
        <input placeholder="I need to...">
        </input>
        <button> Add to List</button>
      </form>
      </>
    );
  }

}

export default Form;
